import React, { useState, useEffect } from "react";
import axios from "axios";
import { useNavigate, useParams } from "react-router-dom";

const EditUser = ({ users, onSave }) => {
  const [name, setName] = useState("");
  const [email, setEmail] = useState("");
  const [gender, setGender] = useState("Male");
  const navigate = useNavigate();
  const { id } = useParams();

  useEffect(() => {
    const user = users.find((user) => user.id === parseInt(id));
    if (user) {
      setName(user.name);
      setEmail(user.email);
      setGender(user.gender);
    }
  }, [id, users]);

  const updateUser = async (e) => {
    e.preventDefault();
    try {
      await axios.patch(`http://localhost:5000/users/${id}`, {
        name,
        email,
        gender,
      });
      onSave({ id: parseInt(id), name, email, gender }); // Update the list
      navigate("/"); // Redirect after saving the update
    } catch (error) {
      console.log(error);
    }
  };

  return (
    <div className="p-8 max-w-xl mx-auto bg-white shadow rounded mt-12">
      <h2 className="text-3xl font-semibold mb-6">Edit Pengguna</h2>
      <form onSubmit={updateUser} className="space-y-6">
        <div>
          <label className="block text-gray-700 mb-1">Nama Pengguna</label>
          <input
            type="text"
            className="border border-gray-300 p-3 w-full rounded focus:outline-none focus:ring focus:ring-blue-300"
            value={name}
            onChange={(e) => setName(e.target.value)}
            required
          />
        </div>
        <div>
          <label className="block text-gray-700 mb-1">Email</label>
          <input
            type="email"
            className="border border-gray-300 p-3 w-full rounded focus:outline-none focus:ring focus:ring-blue-300"
            value={email}
            onChange={(e) => setEmail(e.target.value)}
            required
          />
        </div>
        <div>
          <label className="block text-gray-700 mb-1">Gender</label>
          <select
            className="border border-gray-300 p-3 w-full rounded focus:outline-none focus:ring focus:ring-blue-300"
            value={gender}
            onChange={(e) => setGender(e.target.value)}
          >
            <option value="Male">Male</option>
            <option value="Female">Female</option>
          </select>
        </div>
        <button
          type="submit"
          className="bg-blue-500 text-white px-4 py-3 rounded hover:bg-blue-600 transition w-full"
        >
          Simpan Perubahan
        </button>
      </form>
    </div>
  );
};

export default EditUser;
