import React, { useState } from "react";
import { useNavigate } from "react-router-dom";
import axios from "axios";

const AddUser = ({ onSave }) => {
  const [name, setName] = useState("");
  const [email, setEmail] = useState("");
  const [gender, setGender] = useState("Male");
  const navigate = useNavigate();

  const saveUser = async (e) => {
    e.preventDefault();
    try {
      await axios.post("http://localhost:5000/users", {
        name,
        email,
        gender,
      });
      onSave({ name, email, gender }); // Update state in parent component
      navigate("/"); // Redirect to the main page after saving the user
    } catch (error) {
      console.log(error);
    }
  };

  return (
    <div className="p-8 max-w-xl mx-auto bg-white shadow rounded mt-12">
      <h2 className="text-3xl font-semibold mb-6">Tambah Pengguna</h2>
      <form onSubmit={saveUser} className="space-y-6">
        <div>
          <label className="block text-gray-700 mb-1">Nama Pengguna</label>
          <input
            type="text"
            className="border border-gray-300 p-3 w-full rounded focus:outline-none focus:ring focus:ring-blue-300"
            value={name}
            onChange={(e) => setName(e.target.value)}
            required
          />
        </div>
        <div>
          <label className="block text-gray-700 mb-1">Email</label>
          <input
            type="email"
            className="border border-gray-300 p-3 w-full rounded focus:outline-none focus:ring focus:ring-blue-300"
            value={email}
            onChange={(e) => setEmail(e.target.value)}
            required
          />
        </div>
        <div>
          <label className="block text-gray-700 mb-1">Gender</label>
          <select
            className="border border-gray-300 p-3 w-full rounded focus:outline-none focus:ring focus:ring-blue-300"
            value={gender}
            onChange={(e) => setGender(e.target.value)}
          >
            <option value="Male">Male</option>
            <option value="Female">Female</option>
          </select>
        </div>
        <button
          type="submit"
          className="bg-blue-500 text-white px-4 py-3 rounded hover:bg-blue-600 transition w-full"
        >
          Tambah Pengguna
        </button>
      </form>
    </div>
  );
};

export default AddUser;
